<?phpif (!defined('BASEPATH')) {    exit('No direct script access allowed');}Class Action extends CI_Model {    function GetField($table, $field_name, $where, $get_field) {        $str = false;        $this->db->select("$field_name,$get_field");        $query = $this->db->get_where($table, array($field_name => $where));        if ($query->num_rows() > 0) {            $row = $query->row();            $str = $row->$get_field;        }        return $str;    }    function GetField2($table, $arraywhere, $get_field) {        $str = false;        $this->db->select($get_field);        foreach ($arraywhere as $key => $val) {            $this->db->where($key, $val);        }        $query = $this->db->get($table);        if ($query->num_rows() > 0) {            $row = $query->row();            $str = $row->$get_field;        }        $query->free_result();        return $str;    }}